CREATE TABLE `deliveries` (
  `Id` int NOT NULL AUTO_INCREMENT,
  `DeliveryCode` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL,
  `DeliveryName` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL,
